using System;
using System.Text;
using AlgorithmsAndDataStructures.Algorithms.Cryptography;
using Xunit;

namespace AlgorithmsAndDataStructures.Tests.Algorithm.Cryptography;

public class CesarCipherTests
{
    [Fact]
    public void Base()
    {
        var sut = new CesarCipher();
        const string input = "secret";
        const byte padding = 5;
        var encoded = sut.Encode(input, padding);

        Assert.Equal(input, sut.Decode(encoded, padding));
    }

    [Fact]
    public void LongString()
    {
        var sut = new CesarCipher();
        const string input =
            "";

        const byte padding = 5;
        var encoded = sut.Encode(input, padding);

        Assert.Equal(input, sut.Decode(encoded, padding));
    }

    [Fact]
    public void Fuzzy()
    {
        var sut = new CesarCipher();
        const int inputLength = 10000;
        var stringBuilder = new StringBuilder(inputLength);
        var random = new Random();
        const byte minSymbol = (byte)'0';
        const byte maxSymbol = (byte)'~';

        for (var i = 0; i < inputLength; i++) stringBuilder.Append((char)random.Next(minSymbol, maxSymbol));

        var input = stringBuilder.ToString();
        const byte padding = 5;
        var encoded = sut.Encode(input, padding);

        Assert.Equal(input, sut.Decode(encoded, padding));
    }
}